import React from 'react';
import './App.css';
import Die from "./components/Die";

function App() {

  let [dice, setDice] = React.useState(allNewDice())

  function allNewDice() {
    let randomNumbersArray = []
    for(let i = 0; i < 10; i++) {
      let die = {
        value: Math.floor(Math.random() * 6),
        held: false,
        id: i+1
      }
      randomNumbersArray.push(die)
    }
    return randomNumbersArray
  }

  let diceElements = dice.map(die => <Die key={die.id} die={die} handleClick={dieClick} />)

  function dieClick(clickedId) {
    let myArray = []
    for(let i = 0; i < 10; i++) {
      if(dice[i].id === clickedId) {
        dice[i].held = !dice[i].held
        myArray.push(dice[i])
      } else {
        myArray.push(dice[i])
      }
    }
    setDice(myArray)
  }

  function buttonClick() {
    let myArray = []
    dice.forEach(e => {
      if(e.held) {
        myArray.push(e)
      } else {
        e.value =Math.floor(Math.random() * 6)
        myArray.push(e)
      }
    })
    setDice(myArray)
  }

  let firstDieValue = dice[0].value

  let check = dice.every((e) => {
    console.log(firstDieValue)
    return e.value === firstDieValue
  })

  return (
    <main>
      <h1>Tenzies</h1>
      <p>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
      <div className="dice-container">
        {diceElements}
      </div>
      <button onClick={buttonClick}>Roll</button>
    </main>
  );
}

export default App;
