import React from 'react';
import './App.css';
import Die from "./components/Die";

function App() {

  let [dice, setDice] = React.useState(allNewDice())

  function allNewDice() {
    let newDice = []
    for(let i = 0; i < 10; i++) {
      let die = {
        value: randomNumber(),
        isHeld: false,
        id: i+1
      }
      newDice.push(die)
    }
    return newDice
  }

  let diceElements = dice.map(die => 
    <Die key={die.id} die={die} holdDice={holdDice} />
  )

  function randomNumber() {
    return Math.floor(Math.random() * 6)
  }

  function holdDice(clickedId) {
    setDice(oldDice => {
      oldDice.map
    })
  }

  function rollDice(e) {
    if(e.target.innerHTML === "Roll") {
      let myArray = []
      dice.forEach(e => {
        if(e.isHeld) {
          myArray.push(e)
        } else {
          e.value = randomNumber()
          myArray.push(e)
        }
      })
      setDice(myArray)
    }
    else {
      setDice(allNewDice())
    }
  }

  let firstDieValue = dice[0].value

  let isDone = dice.every((e) => {
    if(e.isHeld) {
      return e.value === firstDieValue
    }
    return false
  })

  return (
    <main>
      <h1>Tenzies</h1>
      <p>Roll until all dice are the same. Click each die to freeze it at its current value between rolls.</p>
      <div className="dice-container">
        {diceElements}
      </div>
      <button onClick={rollDice}>{isDone ? "play again" : "Roll"}</button>
    </main>
  );
}

export default App;
